// In your BikeCard component, modify how you load the image:
import React from 'react';
import MatchlessLogo from '../assets/matchlesslogo.png';

const BikeCard = ({ bike }) => {
  // Dynamically require image based on the filename in the JSON
  const bikeImage = require(`../assets/${bike.image}`); // Use dynamic require

  return (
    <div className="bike-card">
      <div className="top-section">
        <div className="left-section">
          <img src={MatchlessLogo} alt="Matchless Logo" className="logo" />
          <h2 className="model-name">Model {bike.name}</h2>
          <p className="specs">{bike.engine}</p>
          <p className="specs">{bike.suspension}</p>
        </div>
        <div className="right-section">
          <img src={bikeImage.default} alt={`${bike.name} Image`} className="bike-image" />
        </div>
      </div>

      <div className="bottom-section">
        <p className="description">{bike.description}</p>
      </div>
    </div>
  );
};

export default BikeCard;
