import React, { useState, useEffect } from 'react';
import '../css/Bikes.css';

const BikesPage = () => {
  const [activeTab, setActiveTab] = useState('Bikes');
  const [numberPlateInput, setNumberPlateInput] = useState('');
  const [locationDisplay, setLocationDisplay] = useState('');
  const [bikes, setBikes] = useState([]);
  const [numberPlateData, setNumberPlateData] = useState([]);

  // Load the bike data from Bikes.json
  useEffect(() => {
    fetch('/data/Bikes.json')
      .then((res) => res.json())
      .then((data) => setBikes(data))
      .catch((error) => console.error('Error loading bike data:', error));
  }, []);

  // Load the number plate data from NumberPlates.json
  useEffect(() => {
    fetch('/data/NumberPlates.json')
      .then((res) => res.json())
      .then((data) => setNumberPlateData(data))
      .catch((error) => console.error('Error loading number plate data:', error));
  }, []);

  const openTab = (tabName) => {
    setActiveTab(tabName);
  };

  const showBikeLocation = () => {
    getBikeLocation(numberPlateInput).then((location) => {
      setLocationDisplay(`Location: ${location}`);
    });
  };

  const getBikeLocation = async (numberPlate) => {
    const prefix = extractPrefix(numberPlate);
    if (prefix) {
      return getLocationByPrefix(prefix);
    }
    return 'Invalid number plate';
  };

  const extractPrefix = (numberPlate) => {
    const match = numberPlate.match(/^[A-Z]+/i);
    return match ? match[0].slice(-2).toUpperCase() : null;
  };

  const getLocationByPrefix = (prefix) => {
    const entry = numberPlateData.find((item) => item.prefix === prefix);
    return entry ? entry.location : 'Unknown location';
  };

  // Function to render the bike cards
  const createBikeCards = () => {
    console.log('bike.name')
    return bikes.map((bike) => {
      console.log(bike.name)
      const isMatchless = bike.name.includes('Matchless');
      const bikeLogo = isMatchless ? '/images/matchlesslogo.png' : '/images/ajslogo.png';
      const cardStyle = isMatchless ? { backgroundColor: '#f5f5f5', color: '#000' } : { backgroundColor: '#3b6fc4', color: '#fff' };
      const imageStyle = isMatchless ? { backgroundColor: '#f5f5f5' } : { backgroundColor: '#3b6fc4' };

      return (
        <div className="card responsive-card" key={bike.name} style={cardStyle}>
          <div className="card-image" style={imageStyle}>
            <img src={bike.image} alt={`${bike.name} Image`} style={{ width: '100%', height: 'auto' }} />
          </div>
          <div className="card-content">
            <h3>{bike.name}</h3>
            <img src={bikeLogo} alt="Bike Logo" style={{ width: '80px', height: 'auto' }} />
            <p>{bike.description}</p>
            <ul className="bike-stats">
              <li><strong>Year:</strong> {bike.year}</li>
              <li><strong>Engine:</strong> {bike.engine}</li>
              <li><strong>Top Speed:</strong> {bike.topSpeed}</li>
              <li><strong>Weight:</strong> {bike.weight}</li>
            </ul>
          </div>
        </div>
      );
    });
  };

  return (
    <div className="bikes-page">
      <div className="secondary-nav">
        <a
          className={`tablinks ${activeTab === 'Bikes' ? 'active' : ''}`}
          onClick={() => openTab('Bikes')}
        >
          Bikes
        </a>
        <a
          className={`tablinks ${activeTab === 'Table' ? 'active' : ''}`}
          onClick={() => openTab('Table')}
        >
          Number Plates
        </a>
        <a
          className={`tablinks ${activeTab === 'WDBikes' ? 'active' : ''}`}
          onClick={() => openTab('WDBikes')}
        >
          WD Bikes
        </a>
      </div>

      {activeTab === 'Bikes' && (
        <div className="tabcontent">
          <div className="bikes-container">{createBikeCards()}</div>
        </div>
      )}

      {activeTab === 'Table' && (
        <div className="tabcontent">
          <div className="location-tool" style={{ textAlign: 'center', marginBottom: '20px' }}>
            <input
              type="text"
              value={numberPlateInput}
              onChange={(e) => setNumberPlateInput(e.target.value.toUpperCase())}
              placeholder="Reg"
              style={{ padding: '10px', fontSize: '30px' }}
            />
            <button onClick={showBikeLocation} style={{ padding: '10px 20px', fontSize: '16px' }}>
              Search
            </button>
            <p style={{ marginTop: '15px', fontSize: '30px', fontWeight: 'bold' }}>{locationDisplay}</p>
          </div>
          <div className="table-wrapper">
            {/* Table columns will be dynamically generated here */}
          </div>
        </div>
      )}

      {/* WD Bikes tab can go here with similar logic */}
    </div>
  );
};

export default BikesPage;
