import React from 'react';
import '../css/BikeCard.css'; // Import the CSS

const BikeCard = ({ bike }) => {
  return (
    <div className="bike-card">
      {/* Top section */}
      <div className="top-section">
        <div className="left-section">
          <img src="/images/matchlesslogo.png" alt="Matchless Logo" className="logo" />
          <h2 className="model-name">Model {bike.model}</h2>
          <p className="specs">{bike.engine}</p>
          <p className="specs">{bike.suspension}</p>
        </div>
        <div className="right-section">
          <img src={bike.image} alt={`${bike.model} Image`} className="bike-image" />
        </div>
      </div>

      {/* Bottom section for the description */}
      <div className="bottom-section">
        <p className="description">
          {bike.description}
        </p>
      </div>
    </div>
  );
};

export default BikeCard;
