import React from 'react';
import '../css/BikeCard.css'; // Import the CSS
import MatchlessLogo from '../assets/matchlesslogo.png';
import AJSLogo from '../assets/ajsLogo.png';

const BikeCard = ({ bike }) => {
  const bikeImage = require(`../assets/bikes/${bike.image}`).default; // Use dynamic require

  // Determine if it's a Matchless or AJS bike
  const isMatchless = bike.name.toLowerCase().startsWith('matchless');
  const bikeLogo = isMatchless ? MatchlessLogo : AJSLogo;
  const bandColor = isMatchless ? '#f5a9a9' : '#3b6fc4'; // Pink for Matchless, Blue for AJS

  return (
    <div className={`bike-card ${isMatchless ? 'matchless' : 'ajs'}`}>
      {/* Image of the bike */}
      <div className="bike-image-container">
        <img src={bikeImage} alt={`${bike.name} Image`} className="bike-image" />
      </div>

      {/* Bottom section for the description */}
      <div className="bottom-section">
        <p className="description">{bike.description}</p>
      </div>

      {/* Bottom colored band: Pink for Matchless, Blue for AJS */}
      <div className="bottom-colored-section" style={{ backgroundColor: bandColor }}>
        <div className="left-section">
          <h2 className="model-name">{bike.name}</h2>
          <p className="specs">{bike.engine}</p>
          <p className="specs">{bike.suspension}</p>
        </div>
        <div className="right-section">
          <img src={bikeLogo} alt={`${bike.name} Logo`} className="brandLogo" />
        </div>
      </div>
    </div>
  );
};

export default BikeCard;
